INSERT INTO `wp_posts` VALUES (131,1,'2023-04-07 19:18:02','2023-04-07 19:18:02','<!-- wp:paragraph -->\n<p>&gt; ~/.zprofile eval \\\"$(/opt/homebrew/bin/brew shellenv)\\\"\"]]],[1,\"h2\",[[0,[],0,\"Install Yabai\"]]],[1,\"p\",[[0,[],0,\"Run the following command:\"]]],[1,\"p\",[[0,[0],1,\"brew install koekeishiya/formulae/yabai\"]]],[1,\"h2\",[[0,[],0,\"Install Skhd\"]]],[1,\"p\",[[0,[],0,\"Run the following command:\"]]],[1,\"p\",[[0,[0],1,\"brew install koekeishiya/formulae/skhd\"]]],[1,\"h2\",[[0,[],0,\"Create Yabai Config File in Home Directory\"]]],[1,\"p\",[[0,[],0,\"Move to home folder:\"]]],[1,\"p\",[[0,[0],1,\"cd ~\"]]],[1,\"p\",[[0,[],0,\"Create directory for yabai config file and move into it (config will live in \\\".config/yabai/yabairc\\\"):\"]]],[1,\"p\",[[0,[0],1,\"mkdir .config/yabai cd .config/yabai\"]]],[1,\"p\",[[0,[],0,\"Create yabai config file:\"]]],[1,\"p\",[[0,[0],1,\"touch yabairc\"]]],[1,\"h2\",[[0,[],0,\"Add Configuration Options to File\"]]],[1,\"p\",[[0,[],0,\"Open with preferred editor and add the following (\\\"open -t yabairc\\\", \\\"code yabairc\\\" (visual studio code), \\\"vim yabairc\\\", etc...). There are some configuration options that will be available only if you partially disable SIP (Sytem Integrity Protection). All of the options I\'ve configured below will work without disabling SIP.\"]]],[1,\"p\",[[0,[],0,\"Configure default layout to use: Binary Space Partitioning.\"]]],[1,\"p\",[[0,[0,1],1,\"# default layout (can be bsp, stack or float)\"],[0,[],1,\"yabai -m config layout bsp\"]]],[1,\"p\",[[0,[],0,\"Configure how window splits should be made:\"]]],[1,\"p\",[[0,[0,1],1,\"# New window spawns to the right if vertical split, or bottom if horizontal split\"],[0,[],1,\"yabai -m config window_placement second_child\"]]],[1,\"p\",[[0,[],0,\"Configure window padding:\"]]],[1,\"p\",[[0,[0,1],1,\"# padding set to 12px\"],[0,[],1,\"yabai -m config top_padding 12yabai -m config bottom_padding 12yabai -m config left_padding 12yabai -m config right_padding 12yabai -m config window_gap 12\"]]],[1,\"p\",[[0,[],0,\"Configure mouse settings:\"]]],[1,\"p\",[[0,[0,1],1,\"# center mouse on window with focus\"],[0,[],0,\"yabai -m config mouse_follows_focus on \"],[0,[1],1,\"# modifier for clicking and dragging with mouse\"],[0,[],0,\"yabai -m config mouse_modifier alt \"],[0,[1],1,\"# set modifier + left-click drag to move window\"],[0,[],0,\"yabai -m config mouse_action1 move \"],[0,[1],1,\"# set modifier + right-click drag to resize window\"],[0,[],0,\"yabai -m config mouse_action2 resize \"],[0,[1],1,\"# when window is dropped in center of another window, swap them (on edges it will split it)\"],[0,[],1,\"yabai -m mouse_drop_action swap\"]]],[1,\"p\",[[0,[],0,\"Disable specific apps from being managed with yabai. Use this format for the apps you\'d like to disable.\"]]],[1,\"p\",[[0,[0],1,\"yabai -m rule --add app=\\\"^System Settings$\\\" manage=off yabai -m rule --add app=\\\"^Calculator$\\\" manage=off yabai -m rule --add app=\\\"^Karabiner-Elements$\\\" manage=off\"]]],[1,\"h3\",[[0,[],0,\"Start Yabai\"]]],[1,\"p\",[[0,[],0,\"Start it like so:\"]]],[1,\"p\",[[0,[0],1,\"brew services start yabai\"]]],[1,\"p\",[[0,[],0,\"Allow any prompts for accessibility permissions.\"]]],[1,\"p\",[[0,[],0,\"Restart yabai:\"]]],[1,\"p\",[[0,[0],1,\"brew services restart yabai\"]]],[1,\"h3\",[[0,[],0,\"Start Skhd\"]]],[1,\"p\",[[0,[],0,\"Start it like so:\"]]],[1,\"p\",[[0,[0],1,\"brew services start skhd\"]]],[1,\"p\",[[0,[],0,\"Allow any prompts for accessibility permissions.\"]]],[1,\"p\",[[0,[],0,\"Restart skhd:\"]]],[1,\"p\",[[0,[0],1,\"brew services restart skhd\"]]],[1,\"h2\",[[0,[],0,\"Create Skhd Config File in Home Directory\"]]],[1,\"p\",[[0,[],0,\"Move to home folder:\"]]],[1,\"p\",[[0,[0],1,\"cd ~\"]]],[1,\"p\",[[0,[],0,\"Create directory for skhd config file and move into it (config will live in \\\".config/skhd/skhdrc\\\"):\"]]],[1,\"p\",[[0,[0],1,\"mkdir .config/skhd cd .config/skhd\"]]],[1,\"p\",[[0,[],0,\"Create skhd config file:\"]]],[1,\"p\",[[0,[0],1,\"touch skhdrc\"]]],[1,\"h2\",[[0,[],0,\"Add Keyboard Shortcuts To Config File\"]]],[1,\"p\",[[0,[],0,\"These shortcuts are completely up to you, but you can use mine as reference. The way I like to do it is to choose two primary modifiers and a third if necessary. These could be something like \\\"alt\\\", \\\"shift + alt\\\", and \\\"ctrl + alt\\\".\"]]],[1,\"p\",[[0,[],0,\"One recommendation is to change \\\"shift + alt\\\" to \\\"hyper\\\" if you have a \\\"hyper\\\" key on your keyboard (\\\"command\\\" + \\\"control\\\" + \\\"shift\\\" + \\\"alt\\\"). You could configure a \\\"hyper\\\" key (replacing \\\"Caps Lock\\\" for example) with something like \\\"Karabiner-Elements\\\".\"]]],[1,\"h3\",[[0,[],0,\"Changing Focus Shortcuts\"]]],[1,\"h4\",[[0,[],0,\"Change Focus Within Space\"]]],[1,\"p\",[[0,[0,1],1,\"# change window focus within space\"],[0,[],1,\"alt - j : yabai -m window --focus south alt - k : yabai -m window --focus north alt - h : yabai -m window --focus west alt - l : yabai -m window --focus east\"]]],[1,\"h4\",[[0,[],0,\"Change Focus Between Displays\"]]],[1,\"p\",[[0,[0,1],1,\"#change focus between external displays (left and right)\"],[0,[],1,\"alt - s: yabai -m display --focus west alt - g: yabai -m display --focus east\"]]],[1,\"h3\",[[0,[],0,\"Shortcuts For Modifying The Layout\"]]],[1,\"p\",[[0,[0,1],1,\"# rotate layout clockwise\"],[0,[],0,\"shift + alt - r : yabai -m space --rotate 270\"],[0,[1],1,\"# flip along y-axis\"],[0,[],0,\"shift + alt - y : yabai -m space --mirror y-axis \"],[0,[1],1,\"# flip along x-axis\"],[0,[],0,\"shift + alt - x : yabai -m space --mirror x-axis \"],[0,[1],1,\"# toggle window float\"],[0,[],1,\"shift + alt - t : yabai -m window --toggle float --grid 4:4:1:1:2:2\"]]],[1,\"h3\",[[0,[],0,\"Modifying Window Size Shortcuts\"]]],[1,\"p\",[[0,[0,1],1,\"# maximize a window\"],[0,[],0,\"shift + alt - m : yabai -m window --toggle zoom-fullscreen \"],[0,[1],1,\"# balance out tree of windows (resize to occupy same area)\"],[0,[],1,\"shift + alt - e : yabai -m space --balance\"]]],[1,\"h3\",[[0,[],0,\"Shortcuts For Moving Windows Around\"]]],[1,\"h4\",[[0,[],0,\"Swap Windows Within Space\"]]],[1,\"p\",[[0,[0,1],1,\"# swap windows\"],[0,[],1,\"shift + alt - j : yabai -m window --swap south shift + alt - k : yabai -m window --swap north shift + alt - h : yabai -m window --swap west shift + alt - l : yabai -m window --swap east\"]]],[1,\"h4\",[[0,[],0,\"Move Window Within Space And Split\"]]],[1,\"p\",[[0,[0,1],1,\"# move window and split\"],[0,[],1,\"ctrl + alt - j : yabai -m window --warp south ctrl + alt - k : yabai -m window --warp north ctrl + alt - h : yabai -m window --warp west ctrl + alt - l : yabai -m window --warp east\"]]],[1,\"h4\",[[0,[],0,\"Move Window Across Displays\"]]],[1,\"p\",[[0,[0,1],1,\"# move window to display left and right\"],[0,[],1,\"shift + alt - s : yabai -m window --display west; yabai -m display --focus west;shift + alt - g : yabai -m window --display east; yabai -m display --focus east;\"]]],[1,\"h4\",[[0,[],0,\"Move Window To A Space (Desktop/Workspace)\"]]],[1,\"p\",[[0,[0,1],1,\"#move window to prev and next space\"],[0,[],0,\"shift + alt - p : yabai -m window --space prev;shift + alt - n : yabai -m window --space next;\"],[0,[1],1,\"# move window to space #\"],[0,[],1,\"shift + alt - 1 : yabai -m window --space 1;shift + alt - 2 : yabai -m window --space 2;shift + alt - 3 : yabai -m window --space 3;shift + alt - 4 : yabai -m window --space 4;shift + alt - 5 : yabai -m window --space 5;shift + alt - 6 : yabai -m window --space 6;shift + alt - 7 : yabai -m window --space 7;\"]]],[1,\"h3\",[[0,[],0,\"Stop/Start/Restart Yabai\"]]],[1,\"p\",[[0,[0,1],1,\"# stop/start/restart yabai\"],[0,[],1,\"ctrl + alt - q : brew services stop yabai ctrl + alt - s : brew services start yabai ctrl + alt - r : brew services restart yabai\"]]],[1,\"h2\",[[0,[],0,\"That\'s It, You\'re Done!\"]]],[1,\"h2\",[[0,[],0,\"Learning More\"]]],[1,\"p\",[[0,[],0,\"If you\'d like to explore everything you can do with yabai open the man page:\"]]],[1,\"p\",[[0,[0],1,\"man yabai\"]]],[1,\"p\",[[0,[],0,\"--- or ---\"]]],[1,\"p\",[[0,[2],1,\"Yabai Github Repo\"]]],[1,\"p\",[[0,[3],1,\"Yabai Wiki\"]]],[1,\"p\",[[0,[4],1,\"Yabai Documentation\"]]],[1,\"p\",[[0,[5],1,\"Skhd Github Repo\"]]]]}\'&gt;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Configure MacOs Specific Settings</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><!-- wp:list-item -->\n<li>Open Several Desktops (~7) on Your Machine</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Go To Keyboard Settings &gt; Shortcuts &gt; Mission Control</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Expand Mission Control and Turn On Shortcuts for Switching Spaces 1-7 with \"Ctrl + # Of Space\"</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Go to System Settings &gt; Accessibility &gt; Display</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Turn On Reduce Motion</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Go To System Settings &gt; Desktop &amp; Dock &gt; Mission Control</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Turn off \"Automatically Rearrange Spaces Based On Most Recent Use\"</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Personally, I only keep \"Displays Have Separate Spaces\" turned on here, and that\'s what I\'d recommend</li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Open a terminal window</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Open a terminal window on your mac. Could be the default terminal or something else like iTerm2 which is what I\'m currently using.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install Homebrew</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Run the following command:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>/bin/bash -c \"$(curl -fsSL https://raw.githubusercontent.com/Homebrew/install/HEAD/install.sh)\"</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>If necessary, when prompted, enter your password here and press enter. If you haven\'t installed the XCode Command Line Tools, when prompted, press enter and homebrew will install this as well.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Add To Path (Only Apple Silicon Macbooks)</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>After installing, add it to the path. This step shouldn\'t be necessary on Intel macs.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Run the following two commands to do so:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>echo \'eval \"$(/opt/homebrew/bin/brew shellenv)\"\' &gt;&gt; ~/.zprofile eval \"$(/opt/homebrew/bin/brew shellenv)\"</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install Yabai</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Run the following command:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>brew install koekeishiya/formulae/yabai</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install Skhd</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Run the following command:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>brew install koekeishiya/formulae/skhd</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Create Yabai Config File in Home Directory</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Move to home folder:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>cd ~</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Create directory for yabai config file and move into it (config will live in \".config/yabai/yabairc\"):</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>mkdir .config/yabai cd .config/yabai</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Create yabai config file:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>touch yabairc</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Add Configuration Options to File</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Open with preferred editor and add the following (\"open -t yabairc\", \"code yabairc\" (visual studio code), \"vim yabairc\", etc...). There are some configuration options that will be available only if you partially disable SIP (Sytem Integrity Protection). All of the options I\'ve configured below will work without disabling SIP.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Configure default layout to use: Binary Space Partitioning.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code><em># default layout (can be bsp, stack or float)</em>yabai -m config layout bsp</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Configure how window splits should be made:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code><em># New window spawns to the right if vertical split, or bottom if horizontal split</em>yabai -m config window_placement second_child</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Configure window padding:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code><em># padding set to 12px</em>yabai -m config top_padding 12yabai -m config bottom_padding 12yabai -m config left_padding 12yabai -m config right_padding 12yabai -m config window_gap 12</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Configure mouse settings:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code><em># center mouse on window with focus</em>yabai -m config mouse_follows_focus on&nbsp;<em># modifier for clicking and dragging with mouse</em>yabai -m config mouse_modifier alt&nbsp;<em># set modifier + left-click drag to move window</em>yabai -m config mouse_action1 move&nbsp;<em># set modifier + right-click drag to resize window</em>yabai -m config mouse_action2 resize&nbsp;<em># when window is dropped in center of another window, swap them (on edges it will split it)</em>yabai -m mouse_drop_action swap</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Disable specific apps from being managed with yabai. Use this format for the apps you\'d like to disable.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>yabai -m rule --add app=\"^System Settings$\" manage=off yabai -m rule --add app=\"^Calculator$\" manage=off yabai -m rule --add app=\"^Karabiner-Elements$\" manage=off</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Start Yabai</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Start it like so:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>brew services start yabai</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Allow any prompts for accessibility permissions.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Restart yabai:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>brew services restart yabai</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Start Skhd</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Start it like so:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>brew services start skhd</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Allow any prompts for accessibility permissions.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Restart skhd:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>brew services restart skhd</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Create Skhd Config File in Home Directory</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Move to home folder:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>cd ~</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Create directory for skhd config file and move into it (config will live in \".config/skhd/skhdrc\"):</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>mkdir .config/skhd cd .config/skhd</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Create skhd config file:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>touch skhdrc</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Add Keyboard Shortcuts To Config File</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>These shortcuts are completely up to you, but you can use mine as reference. The way I like to do it is to choose two primary modifiers and a third if necessary. These could be something like \"alt\", \"shift + alt\", and \"ctrl + alt\".</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>One recommendation is to change \"shift + alt\" to \"hyper\" if you have a \"hyper\" key on your keyboard (\"command\" + \"control\" + \"shift\" + \"alt\"). You could configure a \"hyper\" key (replacing \"Caps Lock\" for example) with something like \"Karabiner-Elements\".</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Changing Focus Shortcuts</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Change Focus Within Space</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># change window focus within space</em>alt - j : yabai -m window --focus south alt - k : yabai -m window --focus north alt - h : yabai -m window --focus west alt - l : yabai -m window --focus east</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Change Focus Between Displays</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em>#change focus between external displays (left and right)</em>alt - s: yabai -m display --focus west alt - g: yabai -m display --focus east</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Shortcuts For Modifying The Layout</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># rotate layout clockwise</em>shift + alt - r : yabai -m space --rotate 270<em># flip along y-axis</em>shift + alt - y : yabai -m space --mirror y-axis&nbsp;<em># flip along x-axis</em>shift + alt - x : yabai -m space --mirror x-axis&nbsp;<em># toggle window float</em>shift + alt - t : yabai -m window --toggle float --grid 4:4:1:1:2:2</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Modifying Window Size Shortcuts</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># maximize a window</em>shift + alt - m : yabai -m window --toggle zoom-fullscreen&nbsp;<em># balance out tree of windows (resize to occupy same area)</em>shift + alt - e : yabai -m space --balance</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Shortcuts For Moving Windows Around</h3>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Swap Windows Within Space</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># swap windows</em>shift + alt - j : yabai -m window --swap south shift + alt - k : yabai -m window --swap north shift + alt - h : yabai -m window --swap west shift + alt - l : yabai -m window --swap east</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Move Window Within Space And Split</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># move window and split</em>ctrl + alt - j : yabai -m window --warp south ctrl + alt - k : yabai -m window --warp north ctrl + alt - h : yabai -m window --warp west ctrl + alt - l : yabai -m window --warp east</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Move Window Across Displays</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># move window to display left and right</em>shift + alt - s : yabai -m window --display west; yabai -m display --focus west;shift + alt - g : yabai -m window --display east; yabai -m display --focus east;</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 class=\"wp-block-heading\">Move Window To A Space (Desktop/Workspace)</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em>#move window to prev and next space</em>shift + alt - p : yabai -m window --space prev;shift + alt - n : yabai -m window --space next;<em># move window to space #</em>shift + alt - 1 : yabai -m window --space 1;shift + alt - 2 : yabai -m window --space 2;shift + alt - 3 : yabai -m window --space 3;shift + alt - 4 : yabai -m window --space 4;shift + alt - 5 : yabai -m window --space 5;shift + alt - 6 : yabai -m window --space 6;shift + alt - 7 : yabai -m window --space 7;</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Stop/Start/Restart Yabai</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><code><em># stop/start/restart yabai</em>ctrl + alt - q : brew services stop yabai ctrl + alt - s : brew services start yabai ctrl + alt - r : brew services restart yabai</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">That\'s It, You\'re Done!</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Learning More</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>If you\'d like to explore everything you can do with yabai open the man page:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><code>man yabai</code></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>--- or ---</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://github.com/koekeishiya/yabai\">Yabai Github Repo</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://github.com/koekeishiya/yabai/wiki\">Yabai Wiki</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://github.com/koekeishiya/yabai/blob/master/doc/yabai.asciidoc\">Yabai Documentation</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://github.com/koekeishiya/skhd\">Skhd Github Repo</a></p>\n<!-- /wp:paragraph -->','How To Setup And Use The Yabai Tiling Window Manager On Mac','','publish','open','open','','how-to-setup-and-use-the-yabai-tiling-window-manager-on-mac','','','2023-04-07 19:18:16','2023-04-07 19:18:16','',0,'http://deviofuzz.local/?p=131',0,'post','',0);
