INSERT INTO `wp_posts` VALUES (137,1,'2023-04-07 19:21:13','2023-04-07 19:21:13','<!-- wp:heading {\"level\":1} -->\n<h1 class=\"wp-block-heading\"></h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Last updated on&nbsp;Mar 26, 2023</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image\"><img src=\"https://images.unsplash.com/photo-1516031190212-da133013de50?crop=entropy&amp;cs=tinysrgb&amp;fit=max&amp;fm=jpg&amp;ixid=MnwxMTc3M3wwfDF8c2VhcmNofDF8fGl0ZXJtfGVufDB8fHx8MTY3ODAzMDk3MA&amp;ixlib=rb-4.0.3&amp;q=80&amp;w=960\" alt=\"MacOS iTerm2 installation and customization\"/><figcaption class=\"wp-element-caption\">Photo by&nbsp;<a href=\"https://unsplash.com/@pankajpatel?utm_source=ghost&amp;utm_medium=referral&amp;utm_campaign=api-credit\">Pankaj Patel</a>&nbsp;/&nbsp;<a href=\"https://unsplash.com/?utm_source=ghost&amp;utm_medium=referral&amp;utm_campaign=api-credit\">Unsplash</a></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install Homebrew</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Open up a terminal window and install homebrew with the following command:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Configure the Homebrew to Path</h2>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">echo \'eval \"$(/opt/homebrew/bin/brew shellenv)\"\' &gt;&gt; /Users/[username]/.zprofile eval \"$(/opt/homebrew/bin/brew shellenv)\"</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">To Install iTerm2</h2>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">brew install --cask iterm2</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Then you can start using the Iterm2 =)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Installing Git</h2>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">brew install git</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install Oh My Zsh</h2>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">sh -c \"$(curl -fsSL https://raw.githubusercontent.com/ohmyzsh/ohmyzsh/master/tools/install.sh)\"</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install PowerLevel10K Theme for Oh My Zsh</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Clone the PowerLevel10K repo</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">git clone https://github.com/romkatv/powerlevel10k.git $ZSH_CUSTOM/themes/powerlevel10k</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>After install, open the \"~/.zshrc\" file with your editor and change the value of \"ZSH_THEME\", following below</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">ZSH_THEME=\"powerlevel10k/powerlevel10k\"</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>And run the command below to apply the changes.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">source ~/.zshrc</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Configure PowerLevel10K</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Restart iTerm2. You should now be seeing the PowerLevel10K configuration process. If you don\'t, run the following:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">p10k configure</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Follow the instructions for the PowerLevel10K configuration to make your terminal look as desired.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Install ZSH Plugins</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Install zsh-autosuggestions:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">git clone https://github.com/zsh-users/zsh-autosuggestions ${ZSH_CUSTOM:-~/.oh-my-zsh/custom}/plugins/zsh-autosuggestions</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Install zsh-syntax-highlighting:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">git clone https://github.com/zsh-users/zsh-syntax-highlighting.git ${ZSH_CUSTOM:-~/.oh-my-zsh/custom}/plugins/zsh-syntax-highlighting</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Open the \"~/.zshrc\" file in your desired editor and modify the plugins line to what you see below.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">plugins=(git zsh-autosuggestions zsh-syntax-highlighting web-search)</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Load these new plugins by running:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code class=\"\">source ~/.zshrc</code></pre>\n<!-- /wp:code -->','MacOS iTerm2 installation and customization','','publish','open','open','','137-2','','','2023-04-08 22:50:50','2023-04-08 22:50:50','',0,'http://deviofuzz.local/?p=137',0,'post','',0);
