INSERT INTO `wp_posts` VALUES (454,1,'2023-04-08 23:51:04','2023-04-08 23:51:04','<!-- wp:paragraph -->\n<p>Last updated on&nbsp;Feb 26, 2023</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image\"><img src=\"https://images.unsplash.com/photo-1646627927863-19874c27316b?crop=entropy&amp;cs=tinysrgb&amp;fit=max&amp;fm=jpg&amp;ixid=MnwxMTc3M3wwfDF8c2VhcmNofDd8fGRvY2tlcnxlbnwwfHx8fDE2NzczMDk4NzE&amp;ixlib=rb-4.0.3&amp;q=80&amp;w=960\" alt=\"Installing Docker and Pi-hole in your home Linux server machine.\"/><figcaption class=\"wp-element-caption\">Photo by&nbsp;<a href=\"https://unsplash.com/@rubaitulazad?utm_source=ghost&amp;utm_medium=referral&amp;utm_campaign=api-credit\">Rubaitul Azad</a>&nbsp;/&nbsp;<a href=\"https://unsplash.com/?utm_source=ghost&amp;utm_medium=referral&amp;utm_campaign=api-credit\">Unsplash</a></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Hello!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Let\'s start installing Docker in Ubuntu 20.04.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><!-- wp:list-item -->\n<li>First, update all the repositories for Linux packages using apt.</li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:prismatic/blocks {\"language\":\"bash\"} -->\n<pre class=\"wp-block-prismatic-blocks\"><code class=\"language-bash\">sudo apt update</code></pre>\n<!-- /wp:prismatic/blocks -->\n\n<!-- wp:paragraph -->\n<p>2. Install and Run/Enable docker.io package</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:prismatic/blocks {\"language\":\"bash\"} -->\n<pre class=\"wp-block-prismatic-blocks\"><code class=\"language-bash\">sudo apt install docker.io\nsudo systemctl start docker\nsudo systemctl enable docker</code></pre>\n<!-- /wp:prismatic/blocks -->\n\n<!-- wp:paragraph -->\n<p>3. Install Kubernetes - kubectl</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&gt;: sudo apt-get update &amp;&amp; sudo apt-get install -y apt-transport-https\n\n&gt;: curl -s https://packages.cloud.google.com/apt/doc/apt-key.gpg | sudo apt-key add -\n\n&gt;: echo \"deb https://apt.kubernetes.io/ kubernetes-xenial main\" | sudo tee -a /etc/apt/sources.list.d/kubernetes.list\n\n&gt;:sudo apt-get update\n&gt;:sudo apt-get install -y kubectl\n</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>4. Disable DNS server</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&gt;: sudo systemctl stop systemd-resolved.service\n&gt;: sudo systemctl disable systemd-resolved.service\n\n</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>4.1 Open the file /etc/resolv.conf and fix the DNS to 8.8.8.8 (Google), save and close it.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.2 Test if you can ping www.google.com</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5. Create a docker container for Pi-hole</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&gt;: sudo touch pihole.sh\n&gt;: sudo chmod 777 pihole.sh</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>Add the content and run the script below</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code><strong>#!/bin/bash</strong>\n\n# https://github.com/pi-hole/docker-pi-hole/blob/master/README.md\n\ndocker run -d \\\n    --name pihole \\\n    -p 53:53/tcp -p 53:53/udp \\\n    -p 80:80 \\\n    -p 443:443 \\\n    -p 8080:8080 \\\n    -e TZ=\"America/Chicago\" \\\n    -v \"/etc/pihole/\" \\\n    -v \"/etc/dnsmasq.d/\" \\\n    --dns=127.0.0.1 --dns=1.1.1.1 \\\n    --restart=unless-stopped \\\n    pihole/pihole:latest\n\nprintf \'Starting up pihole container \'\nfor i in $(seq 1 20); do\n    if &#91; \"$(docker inspect -f \"{{.State.Health.Status}}\" pihole)\" == \"healthy\" ] ; then\n        printf \' OK\'\n        echo -e \"\\n$(docker logs pihole <strong>2</strong>&gt; /dev/null | grep \'password:\') for your pi-hole: https://${IP}/admin/\"\n        exit 0\n    else\n        sleep 3\n        printf \'.\'\n    fi\n\n    if &#91; $i -eq 20 ] ; then\n        echo -e \"\\nTimed out waiting for Pi-hole start, consult check your container logs for more info (\\`docker logs pihole\\`)\"\n        exit 1\n    fi\ndone;</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&gt;: sudo ./pihole.sh</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>6. Save your generated password for use it later.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Example:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>[i] Assigning random password: evOaEdsu for your pi-hole: https://&lt;your_ip&gt;/admin/login.php</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>7. Change your iptables rules</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&gt;: sudo vim /etc/iptables/rules.v4\n\n</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>Add the lines below:</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>-A INPUT -p tcp --dport 53 -j ACCEPT   \n-A INPUT -p udp --dport 53 -j ACCEPT\n-A INPUT -p tcp -m state --state NEW -m tcp --dport 80 -j ACCEPT</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>Save and close it</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>8. Reboot the Linux.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>9. Go to your pi-hole server!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:embed {\"url\":\"https://\\u003cyour_ip\\u003e/admin\"} -->\n<figure class=\"wp-block-embed\"><div class=\"wp-block-embed__wrapper\">\nhttps://&lt;your_ip>/admin\n</div></figure>\n<!-- /wp:embed -->\n\n<!-- wp:paragraph -->\n<p>Use the password generated above.</p>\n<!-- /wp:paragraph -->','Installing Docker and Pi-hole in your home Linux server machine.','','inherit','closed','closed','','145-revision-v1','','','2023-04-08 23:51:04','2023-04-08 23:51:04','',145,'http://deviofuzz.local/?p=454',0,'revision','',0);
