INSERT INTO `wp_posts` VALUES (466,1,'2023-04-10 17:40:12','2023-04-10 17:40:12','<!-- wp:cover {\"url\":\"http://deviofuzz.local/wp-content/uploads/2021/10/title-7.png\",\"id\":420,\"dimRatio\":0,\"overlayColor\":\"neve-link-color\",\"focalPoint\":{\"x\":\"0.58\",\"y\":\"0.53\"},\"minHeight\":20,\"minHeightUnit\":\"em\",\"align\":\"full\",\"otterConditions\":[]} -->\n<div class=\"wp-block-cover alignfull\" style=\"min-height:20em\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-neve-link-color-background-color has-background-dim-0 has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-420\" alt=\"\" src=\"http://deviofuzz.local/wp-content/uploads/2021/10/title-7.png\" style=\"object-position:58% 53%\" data-object-fit=\"cover\" data-object-position=\"58% 53%\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:spacer {\"height\":\"80px\",\"otterConditions\":[]} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":1,\"textColor\":\"nv-text-dark-bg\",\"otterConditions\":[]} -->\n<h1 class=\"wp-block-heading has-text-align-center has-nv-text-dark-bg-color has-text-color\">About</h1>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"80px\",\"otterConditions\":[]} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:spacer {\"height\":\"80px\",\"otterConditions\":[]} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph -->\n<p>Hi there! I\'m Marcelo, a computer engineer who\'s passionate about a lot of things, including biking, video games, and traveling. I currently live in Norway and work as a hardware engineer, but in my free time, you\'ll often find me programming, tinkering with Raspberry Pi projects, and exploring the latest developme